// Extract error details and user information
const error = $input.item.json.error;
const execution = $input.item.json.execution;

// Try to get user info from the failed execution
let chat_id = null;
let user_id = null;

// Look for chat_id and user_id in execution data
if (execution && execution.data && execution.data.resultData) {
  const runData = execution.data.resultData.runData;

  // Search through nodes for user data
  for (const nodeName in runData) {
    const nodeData = runData[nodeName];
    if (nodeData && nodeData[0] && nodeData[0].data) {
      const mainData = nodeData[0].data.main[0];
      if (mainData && mainData[0] && mainData[0].json) {
        const json = mainData[0].json;
        if (json.chat_id) chat_id = json.chat_id;
        if (json.user_id) user_id = json.user_id;
        if (chat_id && user_id) break;
      }
    }
  }
}

const errorDetails = {
  message: error.message || 'Unknown error',
  type: error.name || 'Error',
  stack: error.stack ? error.stack.substring(0, 500) : 'No stack trace',
  timestamp: new Date().toISOString(),
  workflow: execution?.workflowData?.name || 'Unknown workflow',
  node: error.node?.name || 'Unknown node',
  chat_id,
  user_id
};

// Determine error category
let errorCategory = 'unknown';
let userMessage = 'Oops! Something went wrong. Please try again with /start';

if (error.message) {
  const msg = error.message.toLowerCase();

  if (msg.includes('timeout') || msg.includes('timed out')) {
    errorCategory = 'timeout';
    userMessage = 'Sorry, the request took too long. Please try again! ‚è±Ô∏è';
  } else if (msg.includes('rate limit') || msg.includes('too many requests')) {
    errorCategory = 'rate_limit';
    userMessage = 'Whoa, slow down! You\'ve reached the story limit for now. Please try again in a bit! üê¢';
  } else if (msg.includes('api') || msg.includes('anthropic')) {
    errorCategory = 'api_error';
    userMessage = 'Our story generator is taking a quick nap. Please try again in a moment! üò¥';
  } else if (msg.includes('database') || msg.includes('supabase')) {
    errorCategory = 'database_error';
    userMessage = 'We\'re having trouble saving your story. Please try again! üíæ';
  }
}

return {
  json: {
    ...errorDetails,
    errorCategory,
    userMessage,
    shouldNotifyUser: chat_id !== null
  }
};

// Log error to console (in production, send to logging service)
const errorData = $input.first().json;

console.error('=== STORY BOT ERROR ==');
console.error('Timestamp:', errorData.timestamp);
console.error('Category:', errorData.errorCategory);
console.error('Message:', errorData.message);
console.error('Node:', errorData.node);
console.error('User ID:', errorData.user_id);
console.error('Chat ID:', errorData.chat_id);
console.error('Stack:', errorData.stack);
console.error('=======================');

return { json: errorData };
